// The 'items' variable holds the input data from the previous node.
// It's an array of objects, where each object represents an item in n8n.
// This code assumes the input will always have at least one item and the expected structure.
for (const item of items) {
  // Access the 'q' property from the 'output' object of the current item.
  const queryString = item.json.output.q;

  // Split the string by ', ' (comma followed by a space)
  // Then, map over the resulting array to trim any whitespace from each query.
  const queries = queryString.split(', ').map(query => query.trim());

  // Transform the array of queries into an array of objects,
  // where each object represents a single query as a separate item for n8n.
  // This allows subsequent nodes to process each query individually.
  const outputItems = queries.map(query => ({
    json: {
      query: query // Each query is now under a 'query' key in its own item
    }
  }));

  // Return the array of new items.
  return outputItems;
}

// The 'items' variable holds the input data from the previous node.
// It's an array of objects, where each object contains the 'output' key
// with 'first' and 'second' content items.
const outputItems = [];

// Iterate over each item received by this node.
for (const item of items) {
  // Check if the 'output' property exists and has 'first' and 'second' content.
  if (item.json.output && item.json.output.first) {
    // Add the 'first' content item to our new array.
    // We wrap it in a 'json' key as n8n expects this structure for new items.
    outputItems.push({ json: item.json.output.first });
  }

  if (item.json.output && item.json.output.second) {
    // Add the 'second' content item to our new array.
    outputItems.push({ json: item.json.output.second });
  }
}

// Return the new array of flattened content items.
// Each content item (image prompt, caption, hashtags) will now be a separate item
// in the n8n workflow, ready for individual processing.
return outputItems;
